--B - Funciones de Agregación, HavingUSE Libros--1)SELECT	SUM(L.PRECIO_ORI) Sumatoria,AVG(L.PRECIO_ORI) Promedio,MIN(L.PRECIO_ACT) PrecioMinimo, MAX(L.PRECIO_ACT) PrecioMaxFROM	LIBRO L	WHERE L.EDICION > 1980--2)SELECT	SUM(L.PRECIO_ORI+L.PRECIO_ACT) Sumatoria, AVG(L.PRECIO_ORI+L.PRECIO_ACT) PromedioSumatoria,MIN(L.PRECIO_ACT) PrecioMinimo, MAX(L.PRECIO_ACT) PrecioMaxFROM	LIBRO L	WHERE L.EDICION > 1970--3)SELECT	COUNT(L.TITULO) CantLibros,COUNT(L.TIPO) CantTipos,MIN(L.PRECIO_ORI)MinPrecioOri,MAX(PRECIO_ORI)MaxPrecioOriFROM	LIBRO L	WHERE L.PRECIO_ORI > 45--4)SELECT	L.TIPO CantTipo,SUM(L.PRECIO_ORI) SumPrecioOri,AVG(L.PRECIO_ACT) PromedioPrecioActFROM LIBRO L	WHERE L.EDICION != 1946	GROUP BY L.TIPO,L.EDICION	HAVING SUM(L.PRECIO_ORI) > 40--5) HACERSELECT 	L.TIPO,AVG(L.PRECIO_ORI)PromPrecioOriFROM	LIBRO L	GROUP BY L.TIPO	HAVING AVG(L.PRECIO_ORI) > SUM(L.PRECIO_ORI)--6)SELECT	L.TIPO,SUM(L.PRECIO_ORI)SumatoriaPrecioOri,AVG(L.PRECIO_ACT)PromedioPrecioActFROM	LIBRO L	WHERE L.EDICION BETWEEN 1946 AND 1975	GROUP BY L.TIPO--7)SELECT	L.TIPO,SUM(L.PRECIO_ORI)SumatoriaPrecioOri,AVG(L.PRECIO_ACT)PromedioPrecioActFROM	LIBRO L	WHERE L.EDICION != 1946	GROUP BY L.TIPO	ORDER BY AVG(L.PRECIO_ACT)--8)SELECT	L.TIPO,SUM(L.PRECIO_ORI)SumatoriaPrecioOri,AVG(L.PRECIO_ACT)PromedioPrecioActFROM	LIBRO L	WHERE L.EDICION BETWEEN 1946 AND 1975	GROUP BY L.TIPO--9)SELECT	MAX(L.SALARIO) SalarioPorTrabajoFROM	LECTOR L	GROUP BY L.TRABAJO--10)SELECT	AVG(L.SALARIO) PromedioPorTrabajoFROM	LECTOR L	WHERE L.DIRECCION LIKE '%Cap%'	GROUP BY L.TRABAJO